note_models.css:
.box {
 background: -webkit-linear-gradient(left, #DDEEFF 0%, #CCC 20%, #AAA 45%, #AAA 55%, #CCC 80%, #DDEEFF 100%);
 opacity: 1.00;
 box-shadow: -2px 2px 6px 1px #000;
 border-radius: 8px;
 padding: 50px 15px 30px 15px;
}

.card {
 font-family: arial;
 font-size: 44px;
 text-align: center;
 color: #fff;
 text-shadow: -1px 1px 1px #000;
 background-image: url('_gpne08-25.jpg');
 background-repeat: no-repeat;
 background-position: right bottom;
 background-attachment: fixed;
 margin: 50px 15px;
}

.hint { color: #DDEEFF; text-decoration: none; }

.cloze { font-weight: bold; color: #FF33FF; }

img {width: 55%; height: auto; max-width: 1920px; max-height: 1080px; opacity: 1.00; image-rendering: pixelated;}
.mobile img {width: 50%; height: auto; max-width: 1920px; max-height: 1080px; opacity: 1.00; image-rendering: pixelated;}

.kobi { font-family: kobigraph; font-size: 28px; color: #666; }

.notes { font-size: 24px; color: #666; }

.reading { color: #FFF; }

rt { font-weight: bold; color: #9900CC; }

code#typeans { font-size: 44px; font-family: calibri; }
input#typeans { font-size: 44px; text-align: center; color: #000; border-radius: 8px; max-width: 1000px; padding: 1px 5px; }

.typeGood { font-family: arial; font-size: 44px; color: #009900; background-color: #00ff00; text-shadow: -1px 1px 1px #003300; }
.typeBad { font-family: arial; font-size: 44px; color: #ff0000; background-color: #990000; text-shadow: -1px 1px 1px #330000; }
.typeMissed {font-family: arial; font-size: 44px; color: #666; background-color: #ccc; text-shadow: -1px 1px 1px #000; }

@font-face { font-family: kobigraph; src: url('_kobigraph_slim.ttf'); }

